const ADMIN_CREDENTIALS = {
    email: 'jorgemoreno062006@gmail.com',
    password: '12345678'
};

export const login = async (email, password) => {
    // Simular un pequeño retraso para que parezca una llamada a API
    await new Promise(resolve => setTimeout(resolve, 500));

    // Verificar si es el administrador
    if (email === ADMIN_CREDENTIALS.email && password === ADMIN_CREDENTIALS.password) {
        return {
            email: email,
            name: 'Administrador',
            role: 'admin'
        };
    }
    
    // Para otros usuarios, permitir cualquier email/contraseña
    if (email && password) {
        return {
            email: email,
            name: email.split('@')[0],
            role: 'user'
        };
    }

    throw new Error('Credenciales inválidas');
};

export const register = async (userData) => {
    // Simular un pequeño retraso para que parezca una llamada a API
    await new Promise(resolve => setTimeout(resolve, 500));

    // No permitir registro con el email del administrador
    if (userData.email === ADMIN_CREDENTIALS.email) {
        throw new Error('Este correo electrónico no está disponible');
    }
    
    // Validar que los campos requeridos estén presentes
    if (!userData.email || !userData.username || !userData.password) {
        throw new Error('Todos los campos son requeridos');
    }

    return {
        email: userData.email,
        name: userData.username,
        role: 'user'
    };
};

export const logout = () => {
    // No es necesario hacer nada en el logout
};

export const isAuthenticated = () => {
    return true;
};

export const isAdmin = (user) => {
    return user && user.role === 'admin';
};
