import React, { useState } from 'react';
import AuthLoginForm from './components/AuthLoginForm';
import AuthRegisterForm from './components/AuthRegisterForm';
import AuthForgotPassword from './components/AuthForgotPassword';
import TransportDashboard from './components/TransportDashboard';

const App = () => {
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);

  const handleLogin = (credentials) => {
    // Simular autenticación y asignar un rol de ejemplo
    // En una aplicación real, el backend autenticaría y devolvería el rol
    const authenticatedUser = { email: credentials.email, role: 'admin' }; // Asigna un rol de ejemplo (admin)
    setUser(authenticatedUser);
    // Mostrar el rol del usuario después de la simulación de login
    alert(`Has iniciado sesión como: ${authenticatedUser.role}`);
    setCurrentView('dashboard');
  };

  const handleRegister = (credentials) => {
    // Simular registro
     // En una aplicación real, el backend registraría y podría devolver el rol del nuevo usuario
    const registeredUser = { email: credentials.email, role: 'passenger' }; // Asigna un rol de ejemplo (passenger)
    setUser(registeredUser);
    // Mostrar el rol del usuario después de la simulación de registro
    alert(`Te has registrado y has iniciado sesión como: ${registeredUser.role}`);
    setCurrentView('dashboard');
  };

  const handleResetPassword = (email) => {
    // Simular recuperación de contraseña
    alert(`Se han enviado instrucciones a ${email}`);
    setCurrentView('login');
  };

  const handleLogout = () => {
    setUser(null);
    setCurrentView('login');
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      {currentView === 'login' && (
        <AuthLoginForm
          onLogin={handleLogin}
          onForgotPassword={() => setCurrentView('forgot')}
          onRegister={() => setCurrentView('register')}
        />
      )}
      {currentView === 'register' && (
        <AuthRegisterForm
          onRegister={handleRegister}
          onLogin={() => setCurrentView('login')}
        />
      )}
      {currentView === 'forgot' && (
        <AuthForgotPassword
          onResetPassword={handleResetPassword}
          onLogin={() => setCurrentView('login')}
        />
      )}
      {currentView === 'dashboard' && user && (
        <TransportDashboard user={user} onLogout={handleLogout} />
      )}
    </div>
  );
};

export default App;

